import React, { Component } from 'react'
import ApiService from "../../service/ApiService";

class GetCriterionComponent extends Component {

    constructor(props){
        super(props);
        this.state ={
            id: '',
            name:'',
            description:'',
            message:''
        }
        this.loadCriterion = this.loadCriterion.bind(this);
    }

    componentDidMount() {
        this.loadCriterion();
    }

    loadCriterion() {
        ApiService.fetchCriterionById(window.localStorage.getItem("criterionId"))
            .then((res) => {
                let criterion = res.data;
                this.setState({
                id: criterion.id,
                name: criterion.name,
                description: criterion.description
                })
            });
    }

    onChange = (e) =>
        this.setState({ [e.target.name]: e.target.value });


    render() {
        return (
            <div>
                <h2 className="text-center">{this.state.name}</h2>
                <button className="btn btn-danger" style={{ width: '100px' }} onClick={() => this.addCriterion()}> Add Criterion</button>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th className="hidden">Id</th>
                            <th>Description</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.criteria.map(
                                criterion =>
                                    <tr key={criterion.id}>
                                        <td>{criterion.description}</td>
                                        <td>
                                            <button className="btn btn-success" onClick={() => this.deleteCrtierion(criterion.id)}> Delete</button>
                                            <button className="btn btn-success" onClick={() => this.editCriterion(criterion.id)} style={{ marginLeft: '20px' }}> Edit</button>
                                        </td>
                                    </tr>
                            )
                        }
                    </tbody>
                </table>

            </div>
        );
    }

}

export default GetCriterionComponent;
