import React, { Component } from 'react'
import ApiService from "../../service/ApiService";
class ListCriterionComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            criteria: [],
            message: null
        }
        this.deleteCriterion = this.deleteCriterion.bind(this);
        this.editCriterion = this.editCriterion.bind(this);
        this.addCriterion = this.addCriterion.bind(this);
        this.reloadCriterionList = this.reloadCriterionList.bind(this);
    }

    componentDidMount() {
        this.reloadCriterionList();
    }

    reloadCriterionList() {
        ApiService.fetchCriteria()
            .then((res) => {
                this.setState({ rubrics: res.data })
            });
    }

    deleteCriterion(criterionId) {
        ApiService.deleteCriterion(criterionId)
            .then(res => {
                this.setState({ message: 'Criterion deleted successfully.' });
                this.setState({ rubrics: this.state.rubrics.filter(r => r.id !== criterionId) });
            })

    }

    editRubric(id) {
        window.localStorage.setItem("rubricId", id);
        this.props.history.push('/edit-rubric');
    }

    addRubric() {
        window.localStorage.removeItem("rubricId");
        this.props.history.push('/add-rubric');
    }


    render() {
        return (
            <div>
                <h2 className="text-center">Rubric Details</h2>
                <button className="btn btn-danger" style={{ width: '100px' }} onClick={() => this.addRubric()}> Add Rubric</button>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th className="hidden">Id</th>
                            <th>Name</th>
                            <th>Description</th>
                            <th>Publish Date</th>
                            <th>Operations</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.rubrics.map(
                                rubric =>
                                    <tr key={rubric.id}>
                                        <td>{rubric.name}</td>
                                        <td>{rubric.email}</td>
                                        <td>{rubric.description}</td>
                                        <td>{rubric.publishDate}</td>
                                        <td>
                                            <button className="btn btn-success" onClick={() => this.deleteRubric(rubric.id)}> Delete</button>
                                            <button className="btn btn-success" onClick={() => this.editRubric(rubric.id)} style={{ marginLeft: '20px' }}> Edit</button>
                                        </td>
                                    </tr>
                            )
                        }
                    </tbody>
                </table>

            </div>
        );
    }

}

export default ListCriterionComponent;
